import React, { memo } from 'react'

const Footer =  memo(props =>{
    const filterBtns = [{
        title: 'All',
        isActived: true,
        onClick: () => { },
        link: ''
    },{
        title: 'Active',
        isActived: false,
        onClick: () => { },
        link: '/active'
    },
    {
        title: 'Complete',
        isActived: false,
        onClick: () => { },
        link: '/complete'
    }]
    return(
        <footer className="footer">
            <span className="todo-count">
                <trong>2</trong>
                <span>items</span>
            </span>
            <ul className="filters">
                {
                    filterBtns.map(btn =>(
                        <FilterBtn {...btn}/> 
                    ))
                }
               <FilterBtn /> 
               <FilterBtn /> 
               <FilterBtn /> 
            </ul>
        </footer>
    )
}) 

const FilterBtn = memo(props =>{
    const{ title, onClick, isActived, link} = props
    return(
        <li>
            <a 
                href={`#/${link}`}
                className={`${isActived ? 'select' : ''}`}
                onClick={onClick}
            >
                {title}
            </a>
        </li>
    )
})

export default Footer
