import React, { memo, useState } from 'react';

const Header = memo(() => {
    const[text, setText] = useState('')
    const onAddTodo = (e = {}) => {
        if(e.key === "Enter" && text){
            console.log(text)
        }
    }
    return (
        <header className="header">
            <h1>Todos</h1>
            <input 
                className="new-todo" 
                value = {text}
                onChange = {(e) => setText(e.target.value)}
                onKeyPress= {(e) => onAddTodo(e)}    
            />
        </header>
    )
})
export default Header;
