import React, { memo, useState } from 'react';

const Header = memo(props => {
    const[text, setText] = useState('')
    const {addTodo} = props
    const onAddTodo = (e = {}) => {
        if(e.key === "Enter" && text){
            addTodo({
                id: new Date().valueOf(),
                text,
                isCompleted: false
            })
        }
    }
    return (
        <header className="header">
            <h1>Todos</h1>
            <input 
                className="new-todo" 
                value = {text}
                onChange = {(e) => setText(e.target.value)}
                onKeyPress= {(e) => onAddTodo(e)}    
            />
        </header>
    )
})
export default Header;
